import React from 'react';
import {Switch, Route, Link} from 'react-router-dom';
import logo from './logo.svg';
import './App.css';
import HomePage from './pages/homepage/homepage.component'
import ShopPage from './pages/shop/shop.component.jsx'
import Header from './components/header/header.componentjsx'

function App() {
  return (
    <div className="App">
      <Header />
      {/* route gives us 
        - history
          + history.push gives us ability to change routes
            * useful for onClick methods to history.push('/linkToPage')
        - match
          + "url" 
          + "params.<Id>"
            * helpful for path='/topics/:topicId'
        - location : where we are currently
          + hash and search
          + "pathname" gives us the full pathname
        */}
        <Switch>
          <Route exact path='/' component={HomePage} />
          <Route exact path='/shop' component={ShopPage} />
        </Switch>
    </div>
  );
}

export default App;
