import {takeLatest, put, all, call} from 'redux-saga/effects'

import {UserActionTypes} from './user.types'
import {googleSignInFailure,googleSignInSuccess, emailSignInStart} from './user.actions'
import {auth, googleProvider, createUserProfileDocument} from '../../firebase/firebase.utils'

export function* signInWithGoogle(){
    try{
        const {user} = yield auth.signInWithPopup(googleProvider);
        const userRef = yield call(createUserProfileDocument,user)
        const userSnapshot = yield userRef.get();
        yield put(googleSignInSuccess({id:userSnapshot.id, ...userSnapshot.data()}))
    } catch(err){
        yield put (googleSignInFailure(err))
        console.error(err)
    }
}

export function* onGoogleSignInStart(){
    try{
        yield takeLatest(UserActionTypes.GOOGLE_SIGN_IN_START, signInWithGoogle)
    } catch(err){

    }
}

export function* signInWithEmail({payload:email,password}){
    try{
        yield {user} = auth.signInWithEmailAndPassword(googleProvider);
        yield userRef = call(createUserProfileDocument,user)
        yield userSnapshot = userRef.get()
        yield put(signInWithEmailAndPassword({id:userSnapshot, ...userSnapshot}))
    } catch(err){
        put(emailSignInFailure(err))
    }
}


export function* onEmailSignInStart(){
    yield takeLatest(UserActionTypes.GOOGLE_SIGN_IN_START,signInWithEmail)
}

export function* userSagas(){
    yield all([
        call(onGoogleSignInStart)
    ])
}
