import React from 'react'
import {Spinner, SpinnerOverlay} from './with-spinner.styles'

const withSpinner = WrappedComponent => {
    const Spinner = ({isLoading,...otherProps}) => {
    return isLoading ? (
        <SpinnerOverlay>
            <SpinnerContainer />
        </SpinnerOverlay>):
        (
            <WrappedComponent {...otherProps} />
        )
    }
    return Spinner;
}
export default withSpinner;
